pq

	func (u *UFWeightQuickUnion) Union(p int, q int) {
	    pRoot := u.Find(p)
	    qRoot := u.Find(q)

	    if pRoot == qRoot {
	        return
	    }

	    if u.sz[pRoot] < u.sz[qRoot] {
	        u.id[pRoot] = qRoot
	        u.sz[qRoot] = u.sz[qRoot] + u.sz[pRoot]
	    } else {
	        u.id[qRoot] = pRoot
	        u.sz[pRoot] = u.sz[pRoot] + u.sz[qRoot]
	    }
	    u.count--
	}

FindQuick-Union

	func (u *UFWeightQuickUnion) Find(p int) int {
	    for p != u.id[p] {
	        p = u.id[p]
	    }
	    return p
	}

