	typedef struct {
	    void (*f) (int);
	    int* offset;
	} closure_t;
	void print(int v)
	{
	    printf("%d\n", v);
	}
	void map(closure_t closure, int arr[], int n)
	{
	    for (int i = 0; i < n; ++i) {
	        *(closure.offset) += arr[i];
	        closure.f(*(closure.offset));
	    }
	}
	int main()
	{
	    int offset = 10;
	    closure_t closure = { print, &offset };
	    int arr[] = { 1, 2 };
	    map(closure, arr, sizeof(arr) / sizeof(int));
	    return 0;
	}

